package praticasDia1;

public class Operadores {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x = 15;
		int y = 2;
		float a = x;
		float b = y;
		
		System.out.println("X é " + x + ",Y é " + y);
		System.out.println("A soma de " + x + "+" + y + "= " + (x+y));
		System.out.println("A subtração de " + x + "-" + y + "= " + (x-y));
		System.out.println("A multiplicação de " + x + "*" + y + "= " + (x*y));
		System.out.println("A divisão de " + x + "/" + y + "= " + (a/b));
		System.out.println("O mod d e " + x + "%" + y + "= " + (a%b));
		
			
	}

}
